package com.class17_2;

public class PatternMethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		runPattern();
		
		}

	public static void runPattern() {
		
		for (int r =1; r<=7; r++) {
			for (int c= 1; c<=7; c++) {
				System.out.print(r);
			}
			System.out.println();
		}
	}
	
}