package com.class3;

public class DoubleComparison {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double a = 2.99;
		double b = 1.99;
		
		if (a>=b) {
			System.out.println("Double value a is larger than b");
		}else {
			System.out.println("Double value b is smaller than a");
			
		}
	}
}

